     1→import { create } from 'zustand';
     2→import { devtools, persist } from 'zustand/middleware';
     3→import adwService from '../services/adwService';
     4→import localStorageService from '../services/localStorage';
     5→import stageProgressionService from '../services/stageProgressionService';
     6→import {
     7→  generateAdwId,
     8→  generateWorkflowCommand,
     9→  createTaskData,
    10→  pollWorkflowState,
    11→  updateTaskFromState
    12→} from '../utils/commandGenerator';
    13→
    14→// Work item types
    15→export const WORK_ITEM_TYPES = {
    16→  FEATURE: 'feature',
    17→  CHORE: 'chore',
    18→  BUG: 'bug',
    19→  PATCH: 'patch'
    20→};
    21→
    22→// Available stages for queueing
    23→export const QUEUEABLE_STAGES = [
    24→  { id: 'plan', name: 'Plan', color: 'blue' },
    25→  { id: 'implement', name: 'Implement', color: 'yellow' },
    26→  { id: 'test', name: 'Test', color: 'green' },
    27→  { id: 'review', name: 'Review', color: 'purple' },
    28→  { id: 'document', name: 'Document', color: 'indigo' },
    29→  { id: 'pr', name: 'PR', color: 'pink' }
    30→];
    31→
    32→const initialState = {
    33→  // Project management
    34→  selectedProject: null,
    35→  availableProjects: [],
    36→
    37→  // Task management
    38→  tasks: [],
    39→  taskIdCounter: 1,
    40→
    41→  // Kanban board configuration
    42→  stages: [
    43→    { id: 'backlog', name: 'Backlog', color: 'gray' },
    44→    { id: 'plan', name: 'Plan', color: 'blue' },
    45→    { id: 'build', name: 'Build', color: 'yellow' },
    46→    { id: 'test', name: 'Test', color: 'green' },
    47→    { id: 'review', name: 'Review', color: 'purple' },
    48→    { id: 'document', name: 'Document', color: 'indigo' },
    49→    { id: 'pr', name: 'PR', color: 'pink' },
    50→    { id: 'errored', name: 'Errored', color: 'red' },
    51→  ],
    52→
    53→  // ADW pipeline configurations
    54→  availablePipelines: adwService.getAllPipelines(),
    55→
    56→  // UI state
    57→  showTaskInput: false,
    58→  selectedTaskId: null,
    59→  isLoading: false,
    60→  error: null,
    61→
    62→  // Simplified workflow state
    63→  showCommandModal: false,
    64→  currentCommand: null,
    65→  currentTaskData: null,
    66→  activePollingIntervals: new Map(), // Track polling intervals by adw_id
    67→};
    68→
    69→export const useKanbanStore = create()(
    70→  devtools(
    71→    persist(
    72→      (set, get) => ({
    73→        ...initialState,
    74→
    75→        // Project actions
    76→        selectProject: (project) => {
    77→          set({ selectedProject: project }, false, 'selectProject');
    78→        },
    79→
    80→        addProject: (project) => {
    81→          set((state) => ({
    82→            availableProjects: [...state.availableProjects, project],
    83→          }), false, 'addProject');
    84→        },
    85→
    86→        // Task actions
    87→        createTask: (taskData) => {
    88→          // Generate dynamic pipeline name based on queuedStages
    89→          const generatePipelineName = (queuedStages) => {
    90→            if (!queuedStages || queuedStages.length === 0) {
    91→              return 'adw_unknown';
    92→            }
    93→            return `adw_${queuedStages.join('_')}`;
    94→          };
    95→
    96→          const dynamicPipelineId = generatePipelineName(taskData.queuedStages);
    97→
    98→          const newTask = {
    99→            id: get().taskIdCounter,
   100→            title: taskData.title || '', // Optional title
   101→            description: taskData.description,
   102→            workItemType: taskData.workItemType || WORK_ITEM_TYPES.FEATURE,
   103→            queuedStages: taskData.queuedStages || [],
   104→            pipelineId: dynamicPipelineId, // Dynamic pipeline name based on stages
   105→            pipelineIdStatic: taskData.pipelineId, // Keep static pipelineId for backward compatibility
   106→            stage: 'backlog',
   107→            substage: 'initializing',
   108→            progress: 0,
   109→            createdAt: new Date().toISOString(),
   110→            updatedAt: new Date().toISOString(),
   111→            logs: [],
   112→            metadata: {},
   113→            images: taskData.images || [], // Support for uploaded images
   114→          };
   115→
   116→          set((state) => ({
   117→            tasks: [...state.tasks, newTask],
   118→            taskIdCounter: state.taskIdCounter + 1,
   119→            showTaskInput: false,
   120→          }), false, 'createTask');
   121→
   122→          return newTask;
   123→        },
   124→
   125→        updateTask: (taskId, updates) => {
   126→          set((state) => ({
   127→            tasks: state.tasks.map(task =>
   128→              task.id === taskId
   129→                ? { ...task, ...updates, updatedAt: new Date().toISOString() }
   130→                : task
   131→            ),
   132→          }), false, 'updateTask');
   133→        },
   134→
   135→        deleteTask: (taskId) => {
   136→          set((state) => ({
   137→            tasks: state.tasks.filter(task => task.id !== taskId),
   138→            selectedTaskId: state.selectedTaskId === taskId ? null : state.selectedTaskId,
   139→          }), false, 'deleteTask');
   140→        },
   141→
   142→        moveTaskToStage: (taskId, newStage) => {
   143→          set((state) => ({
   144→            tasks: state.tasks.map(task =>
   145→              task.id === taskId
   146→                ? {
   147→                    ...task,
   148→                    stage: newStage,
   149→                    substage: 'initializing',
   150→                    progress: 0,
   151→                    updatedAt: new Date().toISOString()
   152→                  }
   153→                : task
   154→            ),
   155→          }), false, 'moveTaskToStage');
   156→        },
   157→
   158→        updateTaskProgress: (taskId, substage, progress) => {
   159→          set((state) => ({
   160→            tasks: state.tasks.map(task =>
   161→              task.id === taskId
   162→                ? {
   163→                    ...task,
   164→                    substage,
   165→                    progress,
   166→                    updatedAt: new Date().toISOString()
   167→                  }
   168→                : task
   169→            ),
   170→          }), false, 'updateTaskProgress');
   171→        },
   172→
   173→        addTaskLog: (taskId, logEntry) => {
   174→          set((state) => ({
   175→            tasks: state.tasks.map(task =>
   176→              task.id === taskId
   177→                ? {
   178→                    ...task,
   179→                    logs: [...task.logs, {
   180→                      ...logEntry,
   181→                      timestamp: new Date().toISOString(),
   182→                    }],
   183→                    updatedAt: new Date().toISOString()
   184→                  }
   185→                : task
   186→            ),
   187→          }), false, 'addTaskLog');
   188→        },
   189→
   190→        // Enhanced error handling
   191→        handleError: (error, context = '') => {
   192→          console.error(`KanbanStore Error${context ? ` (${context})` : ''}:`, error);
   193→
   194→          const errorMessage = error instanceof Error ? error.message : String(error);
   195→          const errorDetails = {
   196→            message: errorMessage,
   197→            context,
   198→            timestamp: new Date().toISOString(),
   199→            stack: error instanceof Error ? error.stack : undefined,
   200→          };
   201→
   202→          set({
   203→            error: errorMessage,
   204→            lastError: errorDetails,
   205→          }, false, 'handleError');
   206→
   207→          // Return false to indicate operation failed
   208→          return false;
   209→        },
   210→
   211→        // Retry mechanism for failed operations
   212→        retryOperation: async (operation, maxRetries = 3, delay = 1000) => {
   213→          for (let attempt = 1; attempt <= maxRetries; attempt++) {
   214→            try {
   215→              const result = await operation();
   216→              return result;
   217→            } catch (error) {
   218→              if (attempt === maxRetries) {
   219→                get().handleError(error, `Failed after ${maxRetries} attempts`);
   220→                throw error;
   221→              }
   222→
   223→              console.warn(`Operation failed (attempt ${attempt}/${maxRetries}):`, error);
   224→              await new Promise(resolve => setTimeout(resolve, delay * attempt));
   225→            }
   226→          }
   227→        },
   228→
   229→        // Validation helpers
   230→        validateTask: (task) => {
   231→          const errors = [];
   232→
   233→          // Title is now optional, but if provided, must be reasonable length
   234→          if (task.title && task.title.length > 100) {
   235→            errors.push('Task title must be less than 100 characters');
   236→          }
   237→
   238→          // Description is required
   239→          if (!task.description || task.description.trim().length === 0) {
   240→            errors.push('Task description is required');
   241→          }
   242→
   243→          if (task.description && task.description.length > 2000) {
   244→            errors.push('Task description must be less than 2000 characters');
   245→          }
   246→
   247→          // Work item type validation
   248→          if (!task.workItemType || !Object.values(WORK_ITEM_TYPES).includes(task.workItemType)) {
   249→            errors.push('Valid work item type is required (Feature, Chore, Bug, or Patch)');
   250→          }
   251→
   252→          // Queued stages validation
   253→          if (task.queuedStages && !Array.isArray(task.queuedStages)) {
   254→            errors.push('Queued stages must be an array');
   255→          }
   256→
   257→          if (task.queuedStages && task.queuedStages.length === 0) {
   258→            errors.push('At least one stage must be selected');
   259→          }
   260→
   261→          // Validate each queued stage
   262→          if (task.queuedStages && task.queuedStages.length > 0) {
   263→            const validStageIds = QUEUEABLE_STAGES.map(s => s.id);
   264→            const invalidStages = task.queuedStages.filter(stage => !validStageIds.includes(stage));
   265→            if (invalidStages.length > 0) {
   266→              errors.push(`Invalid stages selected: ${invalidStages.join(', ')}`);
   267→            }
   268→          }
   269→
   270→          return {
   271→            isValid: errors.length === 0,
   272→            errors,
   273→          };
   274→        },
   275→
   276→        validateProject: (project) => {
   277→          const errors = [];
   278→
   279→          if (!project.name || project.name.trim().length === 0) {
   280→            errors.push('Project name is required');
   281→          }
   282→
   283→          if (!project.path || project.path.trim().length === 0) {
   284→            errors.push('Project path is required');
   285→          }
   286→
   287→          // Simulate validation of project structure
   288→          if (!project.hasAgentics) {
   289→            errors.push('Project must have agentics/ directory');
   290→          }
   291→
   292→          if (!project.hasClaude) {
   293→            errors.push('Project must have .claude/ directory');
   294→          }
   295→
   296→          return {
   297→            isValid: errors.length === 0,
   298→            errors,
   299→          };
   300→        },
   301→
   302→        // UI actions
   303→        toggleTaskInput: () => {
   304→          set((state) => ({ showTaskInput: !state.showTaskInput }), false, 'toggleTaskInput');
   305→        },
   306→
   307→        selectTask: (taskId) => {
   308→          set({ selectedTaskId: taskId }, false, 'selectTask');
   309→        },
   310→
   311→        setLoading: (isLoading) => {
   312→          set({ isLoading }, false, 'setLoading');
   313→        },
   314→
   315→        setError: (error) => {
   316→          set({ error }, false, 'setError');
   317→        },
   318→
   319→        clearError: () => {
   320→          set({ error: null }, false, 'clearError');
   321→        },
   322→
   323→        // Simplified workflow actions
   324→        createTaskWithCommand: (taskInput) => {
   325→          try {
   326→            const { selectedProject } = get();
   327→            if (!selectedProject || !selectedProject.handle) {
   328→              throw new Error('No project selected or project handle missing');
   329→            }
   330→
   331→            // Generate ADW ID
   332→            const adwId = generateAdwId();
   333→
   334→            // Create task data for ADW workflow
   335→            const taskData = createTaskData(taskInput, adwId);
   336→
   337→            // Generate command
   338→            const command = generateWorkflowCommand(taskData, selectedProject.handle);
   339→
   340→            // Create task in Kanban
   341→            const newTask = {
   342→              id: get().taskIdCounter,
   343→              ...taskData,
   344→              stage: 'backlog',
