   355→            };
   356→
   357→            set((state) => ({
   358→              tasks: [...state.tasks, newTask],
   359→              taskIdCounter: state.taskIdCounter + 1,
   360→              showTaskInput: false,
   361→              showCommandModal: true,
   362→              currentCommand: command,
   363→              currentTaskData: taskData,
   364→            }), false, 'createTaskWithCommand');
