   366→            return { task: newTask, command, taskData };
   367→          } catch (error) {
   368→            get().handleError(error, 'createTaskWithCommand');
   369→            throw error;
   370→          }
   371→        },
   372→
   373→        showCommandModal: (command, taskData) => {
   374→          set({
   375→            showCommandModal: true,
   376→            currentCommand: command,
   377→            currentTaskData: taskData,
   378→          }, false, 'showCommandModal');
   379→        },
   380→
   381→        hideCommandModal: () => {
   382→          set({
   383→            showCommandModal: false,
   384→            currentCommand: null,
   385→            currentTaskData: null,
   386→          }, false, 'hideCommandModal');
   387→        },
   388→
   389→        startPolling: (adwId, projectHandle) => {
   390→          try {
   391→            // Stop any existing polling for this ADW ID
   392→            get().stopPolling(adwId);
