   447→                task.metadata?.adwId === adwId
   448→                  ? {
   449→                      ...task,
   450→                      ...taskUpdate,
   451→                      updatedAt: new Date().toISOString()
   452→                    }
   453→                  : task
   454→              ),
   455→            }), false, 'updateTaskFromAdwState');
   456→
   457→            // Stop polling if workflow completed or failed
   458→            if (adwState.overall_status === 'completed' || adwState.overall_status === 'failed') {
   459→              get().stopPolling(adwId);
   460→            }
   461→          } catch (error) {
   462→            get().handleError(error, 'updateTaskFromAdwState');
   463→          }
   464→        },
   465→
   466→        findTaskByAdwId: (adwId) => {
   467→          const { tasks } = get();
   468→          return tasks.find(task => task.metadata?.adwId === adwId);
   469→        },
   470→
   471→        // Utility actions
   472→        getTasksByStage: (stage) => {
