import { useState, useEffect } from 'react';
import { CheckCircle, XCircle, Clock, Play, RefreshCw, Terminal } from 'lucide-react';

const ExecutionStatus = ({ taskData, adwId, projectHandle, onClose, isExecuting, executionError }) => {
  const [status, setStatus] = useState('initializing');
  const [progress, setProgress] = useState(0);
  const [logs, setLogs] = useState([]);
  const [currentStage, setCurrentStage] = useState('');

  useEffect(() => {
    if (!isExecuting) return;

    // Simulate status updates for demo purposes
    // In real implementation, this would poll the actual execution status
    const simulateProgress = () => {
      const stages = ['Planning', 'Building', 'Testing', 'Reviewing'];
      let currentStageIndex = 0;
      let currentProgress = 0;

      const interval = setInterval(() => {
        currentProgress += Math.random() * 10;

        if (currentProgress >= 100 * (currentStageIndex + 1) / stages.length) {
          currentStageIndex++;
          if (currentStageIndex >= stages.length) {
            setStatus('completed');
            setProgress(100);
            setCurrentStage('Completed');
            clearInterval(interval);
            return;
          }
        }

        setProgress(Math.min(currentProgress, 100));
        setCurrentStage(stages[currentStageIndex] || 'Initializing');
        setLogs(prev => [...prev, {
          timestamp: new Date().toISOString(),
          level: 'info',
          message: `${stages[currentStageIndex]}: Progress ${Math.round(currentProgress)}%`
        }].slice(-10)); // Keep only last 10 logs
      }, 2000);

      return () => clearInterval(interval);
    };

    if (isExecuting) {
      setStatus('executing');
      return simulateProgress();
    }
  }, [isExecuting]);

  const getStatusIcon = () => {
    switch (status) {
      case 'executing':
        return <RefreshCw className="h-6 w-6 text-blue-600 animate-spin" />;
      case 'completed':
        return <CheckCircle className="h-6 w-6 text-green-600" />;
      case 'failed':
        return <XCircle className="h-6 w-6 text-red-600" />;
      default:
        return <Clock className="h-6 w-6 text-yellow-600" />;
    }
  };

  const getStatusColor = () => {
    switch (status) {
      case 'executing':
        return 'text-blue-600';
      case 'completed':
        return 'text-green-600';
      case 'failed':
        return 'text-red-600';
      default:
        return 'text-yellow-600';
    }
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleTimeString();
  };

  if (executionError) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
        <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center space-x-2">
              <XCircle className="h-6 w-6 text-red-600" />
              <h3 className="text-lg font-semibold text-gray-900">
                Execution Failed
              </h3>
            </div>
            <button
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600"
            >
              <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              </svg>
            </button>
          </div>

          <div className="space-y-4">
            <div>
              <p className="text-sm text-gray-600 mb-2">
                Task: <span className="font-medium">{taskData.title}</span>
              </p>
              <p className="text-sm text-gray-600 mb-4">
                ADW ID: <span className="font-mono font-medium">{adwId}</span>
              </p>
            </div>

            <div className="bg-red-50 rounded-lg p-4">
              <h4 className="font-medium text-red-900 mb-2">Error Details:</h4>
              <p className="text-sm text-red-800">{executionError}</p>
            </div>

            <div className="bg-blue-50 rounded-lg p-4">
              <h4 className="font-medium text-blue-900 mb-2">What to do next:</h4>
              <ol className="text-sm text-blue-800 space-y-1">
                <li>1. Check that the agentics/ directory exists in your project</li>
                <li>2. Ensure the Python orchestrator script is available</li>
                <li>3. Try manual execution or check the project setup</li>
              </ol>
            </div>
          </div>

          <div className="flex justify-end mt-6">
            <button onClick={onClose} className="btn-secondary">
              Close
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-2xl w-full mx-4 max-h-[80vh] overflow-y-auto">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-2">
            {getStatusIcon()}
            <h3 className="text-lg font-semibold text-gray-900">
              Workflow Execution Status
            </h3>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
            </svg>
          </button>
        </div>

        <div className="space-y-4">
          <div>
            <p className="text-sm text-gray-600 mb-2">
              Task: <span className="font-medium">{taskData.title}</span>
            </p>
            <p className="text-sm text-gray-600 mb-4">
              ADW ID: <span className="font-mono font-medium">{adwId}</span>
            </p>
          </div>

          {/* Progress Bar */}
          <div>
            <div className="flex justify-between text-sm mb-2">
              <span className={`font-medium ${getStatusColor()}`}>
                {currentStage || 'Initializing...'}
              </span>
              <span className="text-gray-600">{Math.round(progress)}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div
                className={`h-2 rounded-full transition-all duration-300 ${
                  status === 'executing' ? 'bg-blue-600' :
                  status === 'completed' ? 'bg-green-600' :
                  status === 'failed' ? 'bg-red-600' :
                  'bg-yellow-600'
                }`}
                style={{ width: `${progress}%` }}
              />
            </div>
          </div>

          {/* Status Message */}
          <div className="bg-gray-50 rounded-lg p-4">
            <h4 className="font-medium text-gray-900 mb-2">Status:</h4>
            <p className={`text-sm ${getStatusColor()}`}>
              {status === 'executing' && 'Workflow is running automatically...'}
              {status === 'completed' && 'Workflow completed successfully!'}
              {status === 'failed' && 'Workflow execution failed.'}
              {status === 'initializing' && 'Preparing to execute workflow...'}
            </p>
          </div>

          {/* Real-time Logs */}
          {logs.length > 0 && (
            <div>
              <h4 className="font-medium text-gray-900 mb-2">Recent Activity:</h4>
              <div className="bg-gray-900 text-green-400 p-4 rounded-lg text-sm max-h-32 overflow-y-auto">
                {logs.map((log, index) => (
                  <div key={index} className="flex items-start space-x-2 mb-1">
                    <span className="text-gray-500 text-xs whitespace-nowrap">
                      {formatTimestamp(log.timestamp)}
                    </span>
                    <span className="flex-1">{log.message}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="bg-blue-50 rounded-lg p-4">
            <h4 className="font-medium text-blue-900 mb-2">
              <Play className="inline h-4 w-4 mr-1" />
              Automatic Execution
            </h4>
            <p className="text-sm text-blue-800">
              The workflow is executing automatically in the background.
              The Kanban board will update in real-time as stages complete.
            </p>
          </div>
        </div>

        <div className="flex justify-end mt-6">
          <button onClick={onClose} className="btn-secondary">
            Close
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExecutionStatus;

