import { useState } from 'react';
import { useKanbanStore, WORK_ITEM_TYPES, QUEUEABLE_STAGES } from '../stores/kanbanStore';
import { X, Plus, Image as ImageIcon, Loader, CheckCircle, AlertCircle } from 'lucide-react';
import { useDropzone } from 'react-dropzone';

const TaskInput = () => {
  const {
    createTaskWithCommand,
    toggleTaskInput,
    validateTask,
    startPolling,
    getExecutionConfig,
  } = useKanbanStore();

  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [workItemType, setWorkItemType] = useState(WORK_ITEM_TYPES.FEATURE);
  const [queuedStages, setQueuedStages] = useState(['plan', 'implement']);
  const [images, setImages] = useState([]);
  const [errors, setErrors] = useState([]);
  const [annotatingImage, setAnnotatingImage] = useState(null);
  const [imageAnnotations, setImageAnnotations] = useState({});

  // Loading and execution state
  const [isCreating, setIsCreating] = useState(false);
  const [executionStatus, setExecutionStatus] = useState(null); // 'creating', 'executing', 'success', 'error'
  const [executionMessage, setExecutionMessage] = useState('');

  // Image upload with react-dropzone
  const onDrop = (acceptedFiles) => {
    const newImages = acceptedFiles.map(file => ({
      id: Date.now() + Math.random(),
      file,
      url: URL.createObjectURL(file),
      name: file.name,
      size: file.size,
    }));
    setImages(prev => [...prev, ...newImages]);
  };

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.png', '.jpg', '.jpeg', '.gif', '.webp'],
    },
    maxSize: 5 * 1024 * 1024, // 5MB
  });

  const removeImage = (imageId) => {
    setImages(prev => {
      const updated = prev.filter(img => img.id !== imageId);
      // Clean up object URLs
      const removed = prev.find(img => img.id === imageId);
      if (removed) {
        URL.revokeObjectURL(removed.url);
      }
      return updated;
    });
    // Remove annotations for this image
    setImageAnnotations(prev => {
      const updated = { ...prev };
      delete updated[imageId];
      return updated;
    });
  };

  const handleImageClick = (imageId, event) => {
    const rect = event.target.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    const relativeX = (x / rect.width) * 100;
    const relativeY = (y / rect.height) * 100;

    const annotation = {
      id: Date.now() + Math.random(),
      x: relativeX,
      y: relativeY,
      note: '',
    };

    setImageAnnotations(prev => ({
      ...prev,
      [imageId]: [...(prev[imageId] || []), annotation]
    }));

    // Open annotation edit mode
    setAnnotatingImage({ imageId, annotationId: annotation.id });
  };

  const updateAnnotation = (imageId, annotationId, note) => {
    setImageAnnotations(prev => ({
      ...prev,
      [imageId]: prev[imageId]?.map(ann =>
        ann.id === annotationId ? { ...ann, note } : ann
      ) || []
    }));
  };

  const removeAnnotation = (imageId, annotationId) => {
    setImageAnnotations(prev => ({
      ...prev,
      [imageId]: prev[imageId]?.filter(ann => ann.id !== annotationId) || []
    }));
  };

  const handleStageToggle = (stageId) => {
    setQueuedStages(prev => {
      if (prev.includes(stageId)) {
        return prev.filter(id => id !== stageId);
      } else {
        return [...prev, stageId];
      }
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Clear previous status
    setErrors([]);
    setExecutionStatus('creating');
    setExecutionMessage('Creating task...');
    setIsCreating(true);

    const taskInput = {
      title: title.trim(),
      description: description.trim(),
      type: workItemType,
      priority: 'medium',
      stages: queuedStages,
      images: images.map(img => ({
        ...img,
        annotations: imageAnnotations[img.id] || []
      })),
    };

    try {
      // Get execution config to show appropriate messages
      const config = getExecutionConfig();

      setExecutionMessage(
        config.autoExecute
          ? 'Creating task and starting automatic execution...'
          : 'Creating task...'
      );

      const result = await createTaskWithCommand(taskInput);
      const { task, adwId, autoExecuted } = result;

      if (autoExecuted) {
        setExecutionStatus('executing');
        setExecutionMessage('Task created! Workflow is executing automatically...');

        // Start polling for this task
        if (adwId && task?.metadata?.projectHandle) {
          startPolling(adwId, task.metadata.projectHandle);
        }

        // Show success status briefly, then reset
        setTimeout(() => {
          setExecutionStatus('success');
          setExecutionMessage('Task created and execution started successfully!');

          setTimeout(() => {
            resetForm();
          }, 2000);
        }, 1000);
      } else {
        // Manual execution mode
        setExecutionStatus('success');
        setExecutionMessage('Task created! Manual execution required.');

        // Reset form after brief delay to show success
        setTimeout(() => {
          resetForm();
        }, 1500);
      }

    } catch (error) {
      console.error('Task creation error:', error);
      setExecutionStatus('error');
      setExecutionMessage(`Failed to create task: ${error.message}`);
      setErrors([error.message]);
      setIsCreating(false);
    }
  };

  const resetForm = () => {
    setTitle('');
    setDescription('');
    setWorkItemType(WORK_ITEM_TYPES.FEATURE);
    setQueuedStages(['plan', 'implement']);
    setImages([]);
    setErrors([]);
    setImageAnnotations({});
    setAnnotatingImage(null);
    setIsCreating(false);
    setExecutionStatus(null);
    setExecutionMessage('');
  };

  const workItemTypeOptions = [
    { value: WORK_ITEM_TYPES.FEATURE, label: 'Feature', color: 'bg-blue-100 text-blue-800' },
    { value: WORK_ITEM_TYPES.CHORE, label: 'Chore', color: 'bg-gray-100 text-gray-800' },
    { value: WORK_ITEM_TYPES.BUG, label: 'Bug', color: 'bg-red-100 text-red-800' },
    { value: WORK_ITEM_TYPES.PATCH, label: 'Patch', color: 'bg-yellow-100 text-yellow-800' },
  ];

  return (
    <div className="modal-overlay fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="modal-content bg-white rounded-lg shadow-xl max-w-4xl w-full mx-4 max-h-[90vh] overflow-y-auto">
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <h2 className="text-xl font-semibold text-gray-900">Create New Task</h2>
          <button
            onClick={toggleTaskInput}
            className="p-2 text-gray-400 hover:text-gray-600"
          >
            <X className="h-5 w-5" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {/* Error Messages */}
          {errors.length > 0 && (
            <div className="bg-red-50 border border-red-200 rounded-md p-4">
              <h3 className="text-sm font-medium text-red-800 mb-2">Please fix the following errors:</h3>
              <ul className="list-disc list-inside text-sm text-red-700 space-y-1">
                {errors.map((error, index) => (
                  <li key={index}>{error}</li>
                ))}
              </ul>
            </div>
          )}

          {/* Execution Status */}
          {executionStatus && (
            <div className={`border rounded-md p-4 ${
              executionStatus === 'creating' ? 'bg-blue-50 border-blue-200' :
              executionStatus === 'executing' ? 'bg-yellow-50 border-yellow-200' :
              executionStatus === 'success' ? 'bg-green-50 border-green-200' :
              executionStatus === 'error' ? 'bg-red-50 border-red-200' :
              'bg-gray-50 border-gray-200'
            }`}>
              <div className="flex items-center space-x-3">
                <div className="flex-shrink-0">
                  {executionStatus === 'creating' && <Loader className="h-5 w-5 text-blue-600 animate-spin" />}
                  {executionStatus === 'executing' && <Loader className="h-5 w-5 text-yellow-600 animate-spin" />}
                  {executionStatus === 'success' && <CheckCircle className="h-5 w-5 text-green-600" />}
                  {executionStatus === 'error' && <AlertCircle className="h-5 w-5 text-red-600" />}
                </div>
                <div className="flex-1">
                  <p className={`text-sm font-medium ${
                    executionStatus === 'creating' ? 'text-blue-800' :
                    executionStatus === 'executing' ? 'text-yellow-800' :
                    executionStatus === 'success' ? 'text-green-800' :
                    executionStatus === 'error' ? 'text-red-800' :
                    'text-gray-800'
                  }`}>
                    {executionMessage}
                  </p>
                  {(executionStatus === 'creating' || executionStatus === 'executing') && (
                    <div className="mt-2">
                      <div className="w-full bg-gray-200 rounded-full h-2">
                        <div className={`h-2 rounded-full transition-all duration-1000 ${
                          executionStatus === 'creating' ? 'bg-blue-600 w-1/3' :
                          executionStatus === 'executing' ? 'bg-yellow-600 w-2/3' :
                          'bg-green-600 w-full'
                        }`} />
                      </div>
                    </div>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Optional Title Field */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Task Title (optional)
            </label>
            <input
              type="text"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter task title (optional)..."
              className="input-field"
            />
            <p className="mt-1 text-xs text-gray-500">
              Leave empty to auto-generate from description
            </p>
          </div>

          {/* Work Item Type Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Work Item Type *
            </label>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-3">
              {workItemTypeOptions.map((option) => (
                <label
                  key={option.value}
                  className={`relative cursor-pointer rounded-lg border p-4 text-center transition-all
                    ${workItemType === option.value
                      ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-600'
                      : 'border-gray-200 hover:border-gray-300'
                    }`}
                >
                  <input
                    type="radio"
                    name="workItemType"
                    value={option.value}
                    checked={workItemType === option.value}
                    onChange={(e) => setWorkItemType(e.target.value)}
                    className="sr-only"
                  />
                  <div className={`inline-flex items-center justify-center w-full px-3 py-1 rounded-full text-sm font-medium ${option.color}`}>
                    {option.label}
                  </div>
                </label>
              ))}
            </div>
          </div>

          {/* Stage Queue Selection */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-3">
              Queue Stages *
            </label>
            <p className="text-sm text-gray-500 mb-3">
              Select the stages this task should progress through
            </p>
            <div className="flex flex-wrap gap-3">
              {QUEUEABLE_STAGES.map((stage) => (
                <label
                  key={stage.id}
                  className={`relative cursor-pointer rounded-lg border p-3 transition-all
                    ${queuedStages.includes(stage.id)
                      ? 'border-blue-600 bg-blue-50 ring-2 ring-blue-600'
                      : 'border-gray-200 hover:border-gray-300'
                    }`}
                >
                  <input
                    type="checkbox"
                    checked={queuedStages.includes(stage.id)}
                    onChange={() => handleStageToggle(stage.id)}
                    className="sr-only"
                  />
                  <div className="flex items-center">
                    <div className={`w-3 h-3 rounded-full mr-2 bg-${stage.color}-400`}></div>
                    <span className="text-sm font-medium">{stage.name}</span>
                  </div>
                </label>
              ))}
            </div>
          </div>

          {/* Description */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Description *
            </label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Describe what needs to be done..."
              rows={6}
              className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500 resize-vertical"
            />
            <p className="mt-1 text-xs text-gray-500">
              Plain text description of the task requirements
            </p>
          </div>

          {/* Image Upload */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Attachments
            </label>

            {/* Dropzone */}
            <div
              {...getRootProps()}
              className={`border-2 border-dashed rounded-lg p-6 text-center cursor-pointer transition-colors
                ${isDragActive
                  ? 'border-blue-400 bg-blue-50'
                  : 'border-gray-300 hover:border-gray-400'
                }`}
            >
              <input {...getInputProps()} />
              <ImageIcon className="h-8 w-8 text-gray-400 mx-auto mb-2" />
              {isDragActive ? (
                <p className="text-blue-600">Drop images here...</p>
              ) : (
                <div>
                  <p className="text-gray-600">Drag & drop images here, or click to select</p>
                  <p className="text-xs text-gray-500 mt-1">Supports PNG, JPG, GIF, WebP (max 5MB)</p>
                </div>
              )}
            </div>

            {/* Uploaded Images Preview */}
            {images.length > 0 && (
              <div className="mt-4 space-y-4">
                {images.map((image) => (
                  <div key={image.id} className="border border-gray-200 rounded-lg p-4">
                    <div className="relative inline-block">
                      <img
                        src={image.url}
                        alt={image.name}
                        className="max-w-full h-48 object-contain rounded border border-gray-100 cursor-crosshair"
                        onClick={(e) => handleImageClick(image.id, e)}
                        title="Click to add annotation"
                      />

                      {/* Annotation markers */}
                      {imageAnnotations[image.id]?.map((annotation) => (
                        <div
                          key={annotation.id}
                          className="absolute w-4 h-4 bg-red-500 rounded-full border-2 border-white shadow-lg cursor-pointer transform -translate-x-2 -translate-y-2 hover:bg-red-600"
                          style={{
                            left: `${annotation.x}%`,
                            top: `${annotation.y}%`,
                          }}
                          onClick={(e) => {
                            e.stopPropagation();
                            setAnnotatingImage({ imageId: image.id, annotationId: annotation.id });
                          }}
                          title={annotation.note || 'Click to edit annotation'}
                        >
                          <span className="absolute top-5 left-1/2 transform -translate-x-1/2 bg-black text-white text-xs px-2 py-1 rounded whitespace-nowrap opacity-0 group-hover:opacity-100 transition-opacity z-10">
                            {annotation.note || 'No note'}
                          </span>
                        </div>
                      ))}

                      {/* Remove image button */}
                      <button
                        type="button"
                        onClick={() => removeImage(image.id)}
                        className="absolute top-2 right-2 bg-red-500 text-white rounded-full w-6 h-6 flex items-center justify-center hover:bg-red-600 transition-colors"
                      >
                        <X className="h-3 w-3" />
                      </button>
                    </div>

                    <div className="mt-2 flex items-center justify-between">
                      <p className="text-sm text-gray-600 truncate">{image.name}</p>
                      <p className="text-xs text-gray-500">
                        {imageAnnotations[image.id]?.length || 0} annotation(s)
                      </p>
                    </div>

                    {/* Annotations list */}
                    {imageAnnotations[image.id]?.length > 0 && (
                      <div className="mt-3 space-y-2">
                        <h4 className="text-sm font-medium text-gray-700">Annotations:</h4>
                        {imageAnnotations[image.id].map((annotation, index) => (
                          <div key={annotation.id} className="flex items-start gap-2 text-sm">
                            <span className="inline-block w-3 h-3 bg-red-500 rounded-full mt-1 flex-shrink-0"></span>
                            <span className="flex-1 text-gray-600">
                              {annotation.note || `Annotation ${index + 1}`}
                            </span>
                            <button
                              type="button"
                              onClick={() => setAnnotatingImage({ imageId: image.id, annotationId: annotation.id })}
                              className="text-blue-600 hover:text-blue-800 text-xs"
                            >
                              Edit
                            </button>
                            <button
                              type="button"
                              onClick={() => removeAnnotation(image.id, annotation.id)}
                              className="text-red-600 hover:text-red-800 text-xs"
                            >
                              Remove
                            </button>
                          </div>
                        ))}
                      </div>
                    )}

                    <p className="text-xs text-gray-500 mt-2">
                      Click on the image to add annotations
                    </p>
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Annotation Edit Modal */}
          {annotatingImage && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
              <div className="bg-white rounded-lg shadow-xl max-w-md w-full mx-4 p-6">
                <h3 className="text-lg font-semibold text-gray-900 mb-4">Edit Annotation</h3>
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                      Annotation Note
                    </label>
                    <textarea
                      value={
                        imageAnnotations[annotatingImage.imageId]?.find(
                          ann => ann.id === annotatingImage.annotationId
                        )?.note || ''
                      }
                      onChange={(e) => updateAnnotation(
                        annotatingImage.imageId,
                        annotatingImage.annotationId,
                        e.target.value
                      )}
                      placeholder="Describe what you want to highlight..."
                      rows={3}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
                    />
                  </div>
                  <div className="flex justify-end space-x-3">
                    <button
                      type="button"
                      onClick={() => {
                        removeAnnotation(annotatingImage.imageId, annotatingImage.annotationId);
                        setAnnotatingImage(null);
                      }}
                      className="px-4 py-2 text-sm font-medium text-red-600 bg-red-50 border border-red-200 rounded-md hover:bg-red-100"
                    >
                      Delete
                    </button>
                    <button
                      type="button"
                      onClick={() => setAnnotatingImage(null)}
                      className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-50 border border-gray-200 rounded-md hover:bg-gray-100"
                    >
                      Cancel
                    </button>
                    <button
                      type="button"
                      onClick={() => setAnnotatingImage(null)}
                      className="px-4 py-2 text-sm font-medium text-white bg-blue-600 border border-transparent rounded-md hover:bg-blue-700"
                    >
                      Save
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* Form Actions */}
          <div className="flex justify-end space-x-3 pt-4 border-t border-gray-200">
            <button
              type="button"
              onClick={toggleTaskInput}
              className="btn-secondary"
            >
              Cancel
            </button>
            <button
              type="submit"
              className={`btn-primary flex items-center space-x-2 transition-all ${
                isCreating ? 'opacity-75 cursor-not-allowed' : ''
              }`}
              disabled={!description.trim() || queuedStages.length === 0 || isCreating}
            >
              {isCreating ? (
                <>
                  <Loader className="h-4 w-4 animate-spin" />
                  <span>
                    {executionStatus === 'creating' ? 'Creating...' :
                     executionStatus === 'executing' ? 'Executing...' :
                     'Processing...'}
                  </span>
                </>
              ) : (
                <>
                  <Plus className="h-4 w-4" />
                  <span>Create Task</span>
                </>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default TaskInput;

